import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { FaSpinner } from 'react-icons/fa';

import api from '../../services/api';
import Container from '../../components/Container/index';

import { Loading, Owner, IssueList, IssuesFilter } from './styles';

class Repository extends Component {
  state = {
    repository: {},
    issues: [],
    loading: true,
    issueState: 'closed',
  };

  async componentDidMount() {
    const { match } = this.props;
    const is = localStorage.getItem('issueState');

    if (is) {
      this.setState({ issueState: JSON.parse(is) });
    } else {
      this.setState({ issueState: 'all' });
    }
    const { issueState } = this.state;
    console.log(issueState);

    const repoName = decodeURIComponent(match.params.repository);
    const [repository, issues] = await Promise.all([
      api.get(`/repos/${repoName}`),
      api.get(`/repos/${repoName}/issues`, {
        params: {
          state: JSON.parse(is),
          per_page: 5,
          page: 2,
        },
      }),
    ]);

    this.setState({
      repository: repository.data,
      issues: issues.data,
      loading: false,
    });
  }

  componentDidUpdate(_, prevState) {
    const { issueState } = this.state;
    if (prevState.issueState !== issueState) {
      localStorage.setItem('issueState', JSON.stringify(issueState));
    }
  }

  handleSubmit(e) {
    this.setState({ issueState: e });
  }

  render() {
    const { repository, issues, loading } = this.state;

    if (loading) {
      return (
        <Loading>
          Carregando
          <FaSpinner color="#FFF" size={30} />
        </Loading>
      );
    }

    return (
      <Container>
        <Owner>
          <Link to="/">Voltar aos repositórios</Link>
          <img src={repository.owner.avatar_url} alt={repository.owner.login} />
          <h1>{repository.name}</h1>
          <p>{repository.description}</p>
        </Owner>
        <IssuesFilter>
          <button type="submit" onClick={() => this.handleSubmit('all')}>
            All
          </button>
          <button type="submit" onClick={() => this.handleSubmit('open')}>
            Open
          </button>
          <button type="submit" onClick={() => this.handleSubmit('closed')}>
            Closed
          </button>
        </IssuesFilter>

        <IssueList>
          {issues.map(issue => (
            <li key={String(issue.id)}>
              <img src={issue.user.avatar_url} alt={issue.user.login} />
              <div>
                <strong>
                  <a href={issue.html_url}>{issue.title}</a>
                  {issue.labels.map(label => (
                    <span key={String(label.id)}>{label.name}</span>
                  ))}
                </strong>
                <p>{issue.user.login}</p>
              </div>
            </li>
          ))}
        </IssueList>
      </Container>
    );
  }
}

Repository.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      repository: PropTypes.string,
      state: PropTypes.string,
    }),
  }).isRequired,
};

export default Repository;
