import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { FaSpinner } from 'react-icons/fa';

import api from '../../services/api';
import Container from '../../components/Container/index';

import { Loading, Owner, IssueList, IssuesFilter } from './styles';

class Repository extends Component {
  state = {
    repository: {},
    issues: [],
    loading: true,
    issueState: 'open',
  };

  async componentDidMount() {
    const { match } = this.props;

    const { issueState } = this.state;

    const repoName = decodeURIComponent(match.params.repository);
    const [repository, issues] = await Promise.all([
      api.get(`/repos/${repoName}`),
      api.get(`/repos/${repoName}/issues`, {
        params: {
          state: issueState,
          per_page: 1,
        },
      }),
    ]);

    this.setState({
      repository: repository.data,
      issues: issues.data,
      loading: false,
    });

    const issue = localStorage.getItem('issues');

    if (issue) {
      this.setState({
        issues: JSON.parse(issue),
      });
    }
  }

  async componentDidUpdate(_, prevState) {
    const { issueState, repository } = this.state;
    const repoName = `${repository.owner.login}/${repository.name}`;
    if (prevState.issueState !== issueState) {
      const issue = await api.get(`/repos/${repoName}/issues`, {
        params: {
          state: issueState,
          per_page: 1,
        },
      });
      localStorage.setItem('issue', JSON.stringify(issue.data));
    }
  }

  handleAll = e => {
    e.preventDefault();
    this.setState({ issueState: 'all' });
  };

  handleOpen = e => {
    e.preventDefault();
    this.setState({ issueState: 'open' });
  };

  handleClosed = e => {
    e.preventDefault();
    this.setState({ issueState: 'closed' });
  };

  render() {
    const { repository, issues, loading } = this.state;

    if (loading) {
      return (
        <Loading>
          Carregando
          <FaSpinner color="#FFF" size={30} />
        </Loading>
      );
    }

    return (
      <Container>
        <Owner>
          <Link to="/">Voltar aos reposit√≥rios</Link>
          <img src={repository.owner.avatar_url} alt={repository.owner.login} />
          <h1>{repository.name}</h1>
          <p>{repository.description}</p>
        </Owner>
        <IssuesFilter>
          <button type="submit" onClick={this.handleAll}>
            All
          </button>
          <button type="submit" onClick={this.handleOpen}>
            Open
          </button>
          <button type="submit" onClick={this.handleClosed}>
            Closed
          </button>
        </IssuesFilter>

        <IssueList>
          {issues.map(issue => (
            <li key={String(issue.id)}>
              <img src={issue.user.avatar_url} alt={issue.user.login} />
              <div>
                <strong>
                  <a href={issue.html_url}>{issue.title}</a>
                  {issue.labels.map(label => (
                    <span key={String(label.id)}>{label.name}</span>
                  ))}
                </strong>
                <p>{issue.user.login}</p>
              </div>
            </li>
          ))}
        </IssueList>
      </Container>
    );
  }
}

Repository.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      repository: PropTypes.string,
      state: PropTypes.string,
    }),
  }).isRequired,
};

export default Repository;
