import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { FaSpinner } from 'react-icons/fa';

import api from '../../services/api';
import Container from '../../components/Container/index';

import {
  Loading,
  Owner,
  IssueList,
  IssuesFilter,
  IssuePagination,
} from './styles';

class Repository extends Component {
  state = {
    repository: {},
    issues: [],
    loading: true,
    issueState: '',
    issuePage: 1,
    prevAble: false,
  };

  async componentDidMount() {
    const { match } = this.props;
    const repoName = decodeURIComponent(match.params.repository);
    const repository = await api.get(`/repos/${repoName}`);
    this.setState({
      repository: repository.data,
      loading: false,
    });

    const issue = localStorage.getItem('issueState');
    if (issue) {
      this.setState({ issueState: JSON.parse(issue) });
    }
  }

  componentDidUpdate(_, prevState) {
    const { issueState } = this.state;

    if (prevState.issueState !== issueState) {
      localStorage.setItem('issueState', JSON.stringify(issueState));
    }
  }

  formSumbit = async e => {
    e.preventDefault();
    this.setState({ loading: true });
    const { issueState, repository, issuePage } = this.state;
    if (issuePage === 1) {
      this.setState({ prevAble: false });
    } else {
      this.setState({ prevAble: true });
    }
    const repoName = `${repository.owner.login}/${repository.name}`;

    const issue = await api.get(`/repos/${repoName}/issues`, {
      params: {
        state: issueState,
        per_page: 5,
        page: 6,
      },
    });
    this.setState({
      issues: issue.data,
      loading: false,
    });
  };

  handlePagePrev() {
    const { issuePage } = this.state;
    if (issuePage > 1) {
      this.setState({
        issuePage: Number(issuePage) - 1,
      });
    }
  }

  handlePageNext() {
    const { issuePage } = this.state;

    this.setState({
      issuePage: Number(issuePage) + 1,
    });
  }

  handleSubmit(e) {
    this.setState({ issueState: e });
  }

  render() {
    const { repository, issues, loading } = this.state;

    if (loading) {
      return (
        <Loading>
          Carregando
          <FaSpinner color="#FFF" size={30} />
        </Loading>
      );
    }

    // if (issues.length === 0) {
    //   return (
    //     <Container>
    //       <Owner>
    //         <Link to="/">Voltar aos repositórios</Link>
    //         <img
    //           src={repository.owner.avatar_url}
    //           alt={repository.owner.login}
    //         />
    //         <h1>{repository.name}</h1>
    //         <p>{repository.description}</p>
    //       </Owner>
    //       <IssuesFilter onSubmit={this.formSumbit}>
    //         <button type="submit" onClick={() => this.handleSubmit('all')}>
    //           All
    //         </button>
    //         <button type="submit" onClick={() => this.handleSubmit('open')}>
    //           Open
    //         </button>
    //         <button type="submit" onClick={() => this.handleSubmit('closed')}>
    //           Closed
    //         </button>
    //       </IssuesFilter>
    //     </Container>
    //   );
    // }

    return (
      <Container>
        <Owner>
          <Link to="/">Voltar aos repositórios</Link>
          <img src={repository.owner.avatar_url} alt={repository.owner.login} />
          <h1>{repository.name}</h1>
          <p>{repository.description}</p>
        </Owner>
        <IssuesFilter onSubmit={this.formSumbit}>
          <button type="submit" onClick={() => this.handleSubmit('all')}>
            All
          </button>
          <button type="submit" onClick={() => this.handleSubmit('open')}>
            Open
          </button>
          <button type="submit" onClick={() => this.handleSubmit('closed')}>
            Closed
          </button>
        </IssuesFilter>

        <IssueList>
          {issues.map(issue => (
            <li key={String(issue.id)}>
              <img src={issue.user.avatar_url} alt={issue.user.login} />
              <div>
                <strong>
                  <a href={issue.html_url}>{issue.title}</a>
                  {issue.labels.map(label => (
                    <span key={String(label.id)}>{label.name}</span>
                  ))}
                </strong>
                <p>{issue.user.login}</p>
              </div>
            </li>
          ))}
        </IssueList>

        <IssuePagination onSubmit={this.formSumbit}>
          <button type="submit" onClick={() => this.handlePagePrev()}>
            voltar
          </button>
          <button type="submit" onClick={() => this.handlePageNext()}>
            avançar
          </button>
        </IssuePagination>
      </Container>
    );
  }
}

Repository.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      repository: PropTypes.string,
      state: PropTypes.string,
    }),
  }).isRequired,
};

export default Repository;
