import React, { Component } from 'react';
import { FaGithubAlt, FaPlus, FaSpinner } from 'react-icons/fa';
import { Link } from 'react-router-dom';

import api from '../../services/api';
import Container from '../../components/Container/index';

import { Form, SubmitButton, List } from './styles';

export default class Main extends Component {
  state = {
    newRepo: '',
    repositories: [],
    loading: false,
    notFound: false,
  };

  // Carregar os dados no localStorage
  componentDidMount() {
    const repositories = localStorage.getItem('repositories');
    const notFound = localStorage.getItem('notFound');

    if (repositories) {
      this.setState({
        repositories: JSON.parse(repositories),
      });
    }
    if (notFound) {
      this.setState({
        notFound: JSON.parse(notFound),
      });
    }
  }

  // Salvar os dados no localStorage
  componentDidUpdate(_, prevState) {
    const { repositories, notFound } = this.state;

    if (prevState.repositories !== repositories) {
      localStorage.setItem('repositories', JSON.stringify(repositories));
    }
    if (prevState.notFound !== notFound) {
      localStorage.setItem('notFound', JSON.stringify(notFound));
    }
  }

  handleInputChange = e => {
    this.setState({
      newRepo: e.target.value,
    });
  };

  handleSubmit = async e => {
    e.preventDefault();
    this.setState({ loading: true, notFound: false });
    const { newRepo, repositories } = this.state;
    try {
      const response = await api.get(`/repos/${newRepo}`);

      const data = {
        name: response.data.full_name,
      };

      this.setState({
        repositories: [...repositories, data],
        newRepo: '',
      });
    } catch (error) {
      this.setState({ notFound: true });
      alert(error);
    }
    this.setState({ loading: false });
  };

  render() {
    const { newRepo, loading, repositories, notFound } = this.state;
    return (
      <Container>
        <h1>
          <FaGithubAlt />
          Repositório
        </h1>

        <Form onSubmit={this.handleSubmit} error={notFound ? 1 : 0}>
          <input
            type="text"
            placeholder="Adicionar repositório"
            value={newRepo}
            onChange={this.handleInputChange}
          />

          <SubmitButton loading={loading ? 1 : 0}>
            {loading ? (
              <FaSpinner color="#FFF" size={14} />
            ) : (
              <FaPlus color="#FFF" size={14} />
            )}
          </SubmitButton>
        </Form>
        <List>
          {repositories.map(repository => (
            <li key={repository.name}>
              <span>{repository.name}</span>
              <Link to={`/repository/${encodeURIComponent(repository.name)}`}>
                Detalhes
              </Link>
            </li>
          ))}
        </List>
      </Container>
    );
  }
}
